import base64
import json


RAW_SECRET = None
SECRET = ""

class SecretStorage:
    def __init__(self):
        if SECRET:
            self.secrets = json.loads(base64.b64decode(SECRET))
        else:
            self.secrets = RAW_SECRET

    def to_dict(self):
        return self.secrets

    def get(self, key):
        return self.secrets.get(key)

secret = SecretStorage()